package com.movilidadsostenible.estaciones_service.clients;

import org.springframework.http.HttpStatus;
import org.springframework.http.ResponseEntity;
import org.springframework.stereotype.Component;

import java.util.Map;

@Component
public class CiudadClientFallback implements CiudadClient {
    @Override
    public ResponseEntity<?> getCityById(Integer id) {
        return ResponseEntity.status(HttpStatus.SERVICE_UNAVAILABLE)
                .body(Map.of(
                        "mensaje", "Servicio ciudad-service no disponible temporalmente",
                        "detalle", "Fallback activo para validación de ciudad id=" + id
                ));
    }
}

