package com.movilidadsostenible.usuario.config;

import org.springframework.context.annotation.Bean;
import org.springframework.security.config.annotation.web.builders.HttpSecurity;
import org.springframework.security.config.annotation.web.configuration.EnableWebSecurity;
import org.springframework.security.config.http.SessionCreationPolicy;
import org.springframework.security.web.SecurityFilterChain;

import static org.springframework.security.config.Customizer.withDefaults;

@EnableWebSecurity
public class SecurityConfig {

    @Bean
    SecurityFilterChain securityFilterChain(HttpSecurity http) throws Exception {
        http.authorizeRequests().anyRequest().authenticated()
                .and()
                .sessionManagement().sesseionCreationPolicy(SessionCreationPolicy.STATELESS)
                .and()
                .oauth2Login(oauth2Login -> oauth2Login.loginPage("/oauth2/authorization/usuario-service-client"))
                .oauth2Client(withDefaults());

        return http.build();
    }
}

package com.movilidadsostenible.usuario.config;

import org.springframework.context.annotation.Bean;
import org.springframework.context.annotation.Configuration;
import org.springframework.security.config.annotation.web.builders.HttpSecurity;
import org.springframework.security.config.annotation.web.configuration.EnableWebSecurity;
import org.springframework.security.config.http.SessionCreationPolicy;
import org.springframework.security.web.SecurityFilterChain;

import static org.springframework.security.config.Customizer.withDefaults;

@Configuration
@EnableWebSecurity
public class SecurityConfig {

    @Bean
    public SecurityFilterChain securityFilterChain(HttpSecurity http) throws Exception {
        http
                .authorizeHttpRequests(authorize -> authorize
                        .anyRequest().authenticated()
                )
                .sessionManagement(session -> session
                        .sessionCreationPolicy(SessionCreationPolicy.STATELESS)
                )
                .oauth2Login(withDefaults())
                .oauth2Client(withDefaults());

        return http.build();
    }
}

package com.movilidadsostenible.usuario.services;

import com.movilidadsostenible.usuario.models.entity.User;
import org.springframework.transaction.annotation.Transactional;

import java.util.List;
import java.util.Optional;

public interface UserService {
    List<User> listUsers();
    User save(User user);
    void delete(String uid);
    Optional<User> byId(String uidUser);

    Integer getBalance(String uidUser);
    Integer addBalance(String uidUser, Integer amount);
    Integer subtractBalance(String uidUser, Integer amount);

    // Devuelve true si el usuario NO puede viajar (bloqueado), false si SÍ puede
    boolean isUserBlockedForTravel(String uidUser);

    // Cobra el viaje aplicando reglas de suscripción.
    // totalTripValue: valor total del viaje (en la moneda manejada por balance)
    // excessMinutes: minutos excedentes del viaje
    // uidUser: identificador del usuario
    void chargeTrip(Integer totalTripValue, Integer excessMinutes, String uidUser);
}

