package com.movilidadsostenible.viaje_service.clients.dto;

public class SlotRequestDTO {
    private String idSlot;
    private String padlockStatus;
    private Integer stationId;
    private String bicycleId;

    public SlotRequestDTO() {}

    public SlotRequestDTO(String idSlot, String padlockStatus, Integer stationId, String bicycleId) {
        this.idSlot = idSlot;
        this.padlockStatus = padlockStatus;
        this.stationId = stationId;
        this.bicycleId = bicycleId;
    }

    public String getIdSlot() { return idSlot; }
    public void setIdSlot(String idSlot) { this.idSlot = idSlot; }

    public String getPadlockStatus() { return padlockStatus; }
    public void setPadlockStatus(String padlockStatus) { this.padlockStatus = padlockStatus; }

    public Integer getStationId() { return stationId; }
    public void setStationId(Integer stationId) { this.stationId = stationId; }

    public String getBicycleId() { return bicycleId; }
    public void setBicycleId(String bicycleId) { this.bicycleId = bicycleId; }
}
